import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
// import { url } from '../common/constants';
import axios from "axios";
import SellerMenuBar from "./SellerMenuBar";
import { url } from "../../common/constants";
import { Button } from "reactstrap";

const Product = () => {
  // const [productId, setProductId] = useState(0);
  const [category, setCategory] = useState(0);
  const [seller, setSeller] = useState(0);
  const [productName, setProductName] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [quantity, setQuantity] = useState(0);
  const [unit, setUnit] = useState("");
  const [productPrice, setProductPrice] = useState(0);
  const [productDiscount, setProductDiscount] = useState(0);
  const [productRating, setProductRating] = useState(0);
  const [productImage, setProductImage] = useState(undefined);

  const history = useNavigate();

  // const [seller1, setSeller1] = useState(
  //   JSON.parse(sessionStorage.getItem("seller"))
  // );
  // console.log(sessionStorage.getItem("seller"));

  const product = () => {
    const body = new FormData();

    // body.append("productId", productId);
    body.append("productName", productName);
    body.append("productDescription", productDescription);
    body.append("quantity", quantity);
    body.append("unit", unit);
    body.append("productPrice", productPrice);
    body.append("productDiscount", productDiscount);
    body.append("productRating", productRating);
    body.append("productImage", productImage);
    // body.append("category", category);
    // body.append("sellerId", seller1.sellerId);

    axios
      .post(url + `/products/category/${category}/seller/${seller}`, body)
      .then((response) => {
        const result = response.data;

        console.log(result);
        if (result) {
          alert("succcess");
          // sessionStorage.setItem("seller", JSON.stringify({sellerId : result.sellerId}))
          history.push("/sellerDashboard");

          console.log(result.category);
          console.log(result.seller);
          // console.log(seller1.sellerId);
        } else {
          alert("error");
        }
      });
  };

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("data"));
    console.log(data.name);
    setSeller(data.id);
    console.log(seller);
  }, [seller]);

  const dashboard = () => {
    history("/sellerDashboard");
  };

  return (
    <div className="row">
      <SellerMenuBar />

      <div className="col-8 py-2">
        <h2 className="text-indigo-600" style={{ textAlign: "left" }}>
          Add Product
        </h2>

        <div className="">
          <div>
            {/* <input
              className="form-control mb-4"
              type="hidden"
              required
              style={{ width: "400px" }}
              onChange={(e) => {
                setSeller(localStorage.getItem("data").id);
              }}
            /> */}
            {/* <input
              onChange={(e) => {
                setProductId(e.target.value);
              }}
              className="form-control mb-4"
              type="hidden"
              required
              style={{ width: "400px" }}
            /> */}
          </div>
          <div>
            {/* Product Name:{" "} */}
            <input
              onChange={(e) => {
                setProductName(e.target.value);
              }}
              className="form-control mb-4"
              type="text"
              placeholder="Product Name"
              required
              style={{ width: "400px" }}
            />
          </div>
          <div>
            {/* Product Description:{" "} */}
            <input
              onChange={(e) => {
                setProductDescription(e.target.value);
              }}
              className="form-control mb-4"
              type="text"
              required
              placeholder="Product Description"
              style={{ width: "400px" }}
            />
          </div>
          <div>
            {/* Category Id:{" "} */}
            <input
              onChange={(e) => {
                setCategory(e.target.value);
              }}
              className="form-control mb-4"
              type="text"
              required
              placeholder="Category Id"
              style={{ width: "400px" }}
            />
          </div>

          <div>
            {/* Quantity{" "} */}
            <input
              onChange={(e) => {
                setQuantity(e.target.value);
              }}
              className="form-control mb-4"
              type="text"
              required
              placeholder="Quantity"
              style={{ width: "400px" }}
            />
          </div>
          <div>
  {/* Unit:{" "} */}
  <select
    onChange={(e) => {
      setUnit(e.target.value);
    }}
    className="form-control mb-4"
    required
    style={{ width: "400px" }}
  >
    <option value="" disabled selected>Select Unit</option>
    <option value="kg">Kilograms (kg)</option>
    <option value="g">Grams (g)</option>
    <option value="l">Liters (l)</option>
    <option value="ml">Milliliters (ml)</option>
    <option value="dz">Dozen (doz)</option>
    {/* Add more unit options as needed */}
  </select>
</div>

          <div>
            {/* Product Price:{" "} */}
            <input
              onChange={(e) => {
                setProductPrice(e.target.value);
              }}
              className="form-control mb-4"
              type="text"
              required
              placeholder=" Product Price"
              style={{ width: "400px" }}
            />
          </div>
          <div>
            {/* Product Discount:{" "} */}
            <input
              onChange={(e) => {
                setProductDiscount(e.target.value);
              }}
              className="form-control mb-4"
              type="text"
              required
              placeholder="Product Discount"
              style={{ width: "400px" }}
            />
          </div>

          <div>
            {/* Product Rating:{" "} */}
            <input
              onChange={(e) => {
                setProductRating(e.target.value);
              }}
              className="form-control mb-4"
              type="text"
              placeholder="Product Rating"
              required
              style={{ width: "400px" }}
            />
          </div>
          <div>
            {/* productImage:{" "} */}
            <input
              onChange={(e) => {
                setProductImage(e.target.files[0]);
              }}
              className="form-control mb-4"
              type="file"
              required
              placeholder="ProductImage"
              style={{ width: "400px" }}
            />
          </div>

          <div style={{ textAlign: "left" }}>
            <button onClick={product} className="btn btn-info">
              Add Product
            </button>
            {"   "}
            {"   "}
            {"  "}
            {"  "}
            {/* <a
              href="/sellerDashboard"
              className="text-indigo-600 font-medium hover:text-indigo-500 p-3"
            >
              <Button> Back To DashBoard</Button>{" "}
            </a> */}
            <Button onClick={dashboard}>Back To DashBoard</Button>{" "}
          </div>
        </div>
      </div>
    </div>
  );
};
export default Product;
